from google.cloud import texttospeech

# Set the target language
target_language = "hi-IN"

# Load the service account credentials from the key.json file
os.environ["GOOGLE_APPLICATION_CREDENTIALS"] = "key.json"

client = texttospeech.TextToSpeechClient()
audio_chunks = []
output_dir = "audio_chunks"  # Create a directory to store individual audio chunks
os.makedirs(output_dir, exist_ok=True)

chunk_size = 50
chunks = [input_text[i:i + chunk_size] for i in range(0, len(input_text), chunk_size)]
chunk_count = 0

for chunk in chunks:
    synthesis_input = texttospeech.SynthesisInput(text=chunk)

    voice_config = texttospeech.VoiceSelectionParams(
        language_code=target_language,
        name="hi-IN-Wavenet-A",
    )

    audio_config = texttospeech.AudioConfig(
        audio_encoding=texttospeech.AudioEncoding.LINEAR16,
    )

    response = client.synthesize_speech(
        input=synthesis_input, voice=voice_config, audio_config=audio_config
    )

    # Save each chunk as a separate WAV file
    chunk_file_path = os.path.join(output_dir, f"chunk_{chunk_count}.wav")
    with open(chunk_file_path, "wb") as chunk_file:
        chunk_file.write(response.audio_content)

    chunk_count += 1

print("Text-to-speech conversion complete. Audio chunks saved to 'audio_chunks' directory.")

